mapper.update:
    update od_order_settlement


		income_status = #{incomeStatus,jdbcType=INTEGER},


        payment_status = #{paymentStatus,jdbcType=INTEGER},


    where 1 = 1

        and income_billid = #{incomeBillId,jdbcType=VARCHAR}


        and payment_applyid = #{paymentApplyid,jdbcType=VARCHAR}

mapper.select:
	select

	from od_order_settlement
	where is_del = 0

		and order_id = #{orderId,jdbcType=VARCHAR}


		and income_billid = #{incomeBillId,jdbcType=VARCHAR}


		and payment_applyid = #{paymentApplyId,jdbcType=VARCHAR}



		and order_code like #{orderCodeLike,jdbcType=VARCHAR}



		and is_inner = #{isInner,jdbcType=BIT}



       	and settlement_parent_id = #{settlementParentId,jdbcType=VARCHAR}

   	SELECT IFNULL(SUM(s.income_amount_cny),0) FROM od_order o,od_order_settlement s
	WHERE o.id = s.order_id AND o.customer_id = #{customerId,jdbcType=VARCHAR}
	AND s.income_status IN (1,2)
