       and bill_no like concat('%',#{billNo,jdbcType=VARCHAR},'%')


       and trade_goods_remark like concat('%',#{productName,jdbcType=VARCHAR},'%')


       and date_format(expected_arrival_time,'%Y-%m-%d')  >=  #{startArrivalTime}


       and date_format(expected_arrival_time,'%Y-%m-%d')  <=  #{endArrivalTime}


